/*The forwardfortime function makes to robot move forwards
at a specified speed for a specified time. */
void forwardfortime (int speed, int time) {
  motor[leftmotor] = speed;
  motor[rightmotor] = speed;
  wait1Msec(time);
  motor[leftmotor] = 0;
  motor[rightmotor] = 0;
}
/*The setmotorspeed function simply sets the speed of both
motors at once. Saving space and time. (For the coder)*/
void setmotorspeed (int speed) {
  motor[leftmotor] = speed;
  motor[rightmotor] = speed;
}
void gotobin () {
  while (SensorValue(encoderright) > 5 && SensorValue(encoderleft) > 5) {
    setmotorspeed(-127);
  }
  setmotorspeed(0);
  while (SensorValue(encoderright) > 5) {
    motor[rightmotor] = -127;
  }
  setmotorspeed(0);
  while (SensorValue(encoderleft) > 5) {
    motor[leftmotor] = -127;
  }
  setmotorspeed(0);
}

